mapper.select:
select s.assetId, s.assetCode, s.assetTempCode, s.assetAbbreviation, s.transfer, s.assetType, s.timeOfSubmissionOfRegistration, s.userid, s.loginName, e.creditCode, e.address, e.shortName from (select a.id assetId, a.assetCode, a.assetTempCode, a.assetAbbreviation, a.transfer, a.assetType, a.timeOfSubmissionOfRegistration, f.id userid, f.loginName from asset a join foregrounduser f on a.userid = f.id
and a.assetCode = #{assetCode}
and a.assetTempCode = #{assetTempCode}
and a.assetAbbreviation = #{assetAbbreviation}
and a.timeOfSubmissionOfRegistration >= #{minTime}
and a.timeOfSubmissionOfRegistration <= #{maxTime}
and f.loginname = #{loginName}
and a.state = 3
) s join enterprisecertif e on s.userid = e.userid
and e.shortName = #{shortName}
order by s.timeOfSubmissionOfRegistration asc
